// same as leetcode 79

function wordPuzzle(grid: string[][], target: string): boolean {
  const cache = new Map<string, boolean>();
  if (grid.length === 0) return false;
  const m = grid.length, n = grid[0].length;
  const state = new Map<string, boolean>()

  function findWord(state: Map<string, boolean>, i: number, j: number): boolean {
    // console.log(i, j);

    if (i < 0 || i >= m) return false;
    if (j < 0 || j >= n) return false;
    if (target.charAt(state.size) !== grid[i][j]) return false;

    state.set(`${i}->${j}`, true);
    if (target.length === state.size) return true;

    let left: boolean = false, right: boolean = false, top: boolean = false, bottom: boolean = false;
    if (i - 1 >= 0 && i - 1 < m && !state.has(`${i - 1}->${j}`)) {
      if (cache.has(`${i - 1}cache${j}`) === true) {
        left = !!cache.get(`${i - 1}cache${j}`)
      } else {
        left = findWord(state, i - 1, j)
        cache.set(`${i - 1}cache${j}`, left)
      }
    }
    if (i + 1 >= 0 && i + 1 < m && !state.has(`${i + 1}->${j}`)) {
      if (cache.has(`${i + 1}cache${j}`) === true) {
        right = !!cache.get(`${i + 1}cache${j}`)
      } else {
        right = findWord(state, i + 1, j)
        cache.set(`${i + 1}cache${j}`, right)
      }
    }
    if (j - 1 >= 0 && j - 1 < n && !state.has(`${i}->${j - 1}`)) {
      if (cache.has(`${i}cache${j - 1}`) === true) {
        top = !!cache.get(`${i}cache${j - 1}`)
      } else {
        top = findWord(state, i, j - 1)
        cache.set(`${i}cache${j - 1}`, top)
      }
    }
    if (j + 1 >= 0 && j + 1 < n && !state.has(`${i}->${j + 1}`)) {
      if (cache.has(`${i}cache${j + 1}`) === true) {
        bottom = !!cache.get(`${i}cache${j + 1}`)
        console.log('cachr', cache);

      } else {
        bottom = findWord(state, i, j + 1)
        cache.set(`${i}cache${j + 1}`, bottom)
      }
    }
    // console.log({ i, j, chart: grid[i][j] });

    state.delete(`${i}->${j}`);
    console.log(JSON.stringify(cache));
    console.log({ left, right, top, bottom });

    return left || right || top || bottom
  }

  for (let i = 0; i < m; i++) {
    for (let j = 0; j < n; j++) {
      const cur = grid[i][j]
      if (target.charAt(0) === cur) {
        // console.log('start:', { i, j });
        const find = findWord(state, i, j)
        if (find === true) { return true; }
      }
    }
  }

  return false
};

